# Knowledge Representation using a dictionary of student information
students = {
    'John': {
        'age': 20,
        'gender': 'Male',
        'major': 'Computer Science'
    },
    'Jane': {
        'age': 22,
        'gender': 'Female',
        'major': 'Electrical Engineering'
    },
    'Alex': {
        'age': 19,
        'gender': 'Non-binary',
        'major': 'Data Science'
    }
}

# Data Preparation using a list of student scores
math_scores = [85, 90, 78]

# Printing the knowledge representation
print("Knowledge Representation:")
for name, info in students.items():
    print(f"Name: {name}, Age: {info['age']}, Gender: {info['gender']}, Major: {info['major']}")

# Data Preparation - calculating the average math score
average_math_score = sum(math_scores) / len(math_scores)
print("\nData Preparation:")
print("Math Scores:", math_scores)
print("Average Math Score:", average_math_score)
